import logoquest from './../../images//logoQuest.svg';
import './CSS/Perguntas.css';

import CadastroForm from '../Formularios/CadastroForm';
import LinkButton from '../layout/LinkButton';

function Perguntas() {
    var now = new Date().getTime();
    
    // Find the distance between now and the count down date
    var distance = countDownDate - now;
      
    // Time calculations for days, hours, minutes and seconds
    var days = Math.floor(distance / (1000 * 60 * 60 * 24));
    var hours = Math.floor((distance % (1000 * 60 * 60 * 24)) / (1000 * 60 * 60));
    var minutes = Math.floor((distance % (1000 * 60 * 60)) / (1000 * 60));
    var seconds = Math.floor((distance % (1000 * 60)) / 1000);
    return(
    <div id='Perguntas'>
        <div className='perguntas-FundoTotal'>
            <div className="perguntas-FundoApp" >
                <div className='perguntas-FundoBranco'>
                    <div id='Title'>
                    <h2>Sua vez! Escolha um ficha:</h2>
                    <span id="seconds">Teste</span>
                    </div>
                    <div id='fichas'>
                        <button className='ficha'>1</button>
                        <button className='ficha'>2</button>
                        <button className='ficha'>3</button>
                        <button className='ficha'>4</button>
                        <button className='ficha'>5</button>
                    </div>

                        

                    <div id='Caixa-Perguntas'>
                        Perguntas
                    </div>

                    <div id='alternativas'>
                        <button className='alternativa'>Alternativa 1</button>
                        <button className='alternativa'>Alternativa 2</button>
                        <button className='alternativa'>Alternativa 3</button>
                        <button className='alternativa'>Alternativa 4</button>
                        <button className='alternativa'>Alternativa 5</button>
                    </div>

                </div>
            </div>
        </div>
    </div>
    ) 
}

export default Perguntas
