import logoquest from './../../images//logoQuest.svg';
import './CSS/Perguntas.css';
import ficha_1 from './../../images//ficha1.svg';

import CadastroForm from '../Formularios/CadastroForm';
import LinkButton from '../layout/LinkButton';

function Perguntas() {

    function teste(){
       var testando = document.getElementById('teste').value;
        console.log(testando);
    }

    function abrirPopUp(){
        document.getElementById('popup').style.display = 'block';
    }




    function answer(){
        var resp = document.getElementsById('teste').value;
        let el = document.getElementsById('teste');
        if(resp)
            el.style.color = 'red';
            
    }

/*
    let el = document.getElementsById('teste');
    // alterando uma propriedade
    el.style.color = 'red';
    // varias propriedades
    el.style.cssText =
    'color: blue;' +
    'background-color: yellow;' +
    'border: 1px solid magenta';
    }
*/
    var countDownDate = new Date("Jan 6, 2024 15:37:").getTime();
    // Update the count down every 1 second
    var x = setInterval(function() {
      // Get today's date and time
      var now = new Date().getTime() + 30;
      // Find the distance between now and the count down date
      var distance = countDownDate - now;
      // Time calculations for days, hours, minutes and seconds
      var seconds = Math.floor((distance % (1000 * 30)) / 1000);
      // Output the result in an element with id="demo"
      document.getElementById("seconds").innerHTML =  seconds + "s ";
      // If the count down is over, write some text 
      if (distance < 0) {
        clearInterval(x);
        document.getElementById("seconds").innerHTML = "EXPIRED";
      }
    });

    
    return(
    <div>
        <button onClick={abrirPopUp}>Abrir Pop-up</button>
            <div id='Perguntas'>
                
                <div className='perguntas-FundoTotal'>
                    <div className="perguntas-FundoApp" >
                        <div id='popup' className='perguntas-FundoBranco'>
                            <div id='Title'>
                            <h2>Sua vez! Escolha um ficha:</h2>
                            <span id="seconds">Teste</span>
                            </div>
                            <div id='fichas'>
                                <button  className='ficha'>1</button>
                                <button className='ficha'>2</button>
                                <button className='ficha'>3</button>
                                <button className='ficha'>4</button>
                                <button className='ficha'>5</button>
                            </div>
                    

                            <div id='Caixa-Perguntas'>
                                Perguntas
                            </div>

                            <div id='alternativas'>
                                <button className='alternativa' id='teste' onClick={answer} type="submit" value={true}>Alternativa 1</button>
                                <button className='alternativa' type="submit">Alternativa 2</button>
                                <button className='alternativa' type="submit">Alternativa 3</button>
                                <button className='alternativa' type="submit">Alternativa 4</button>
                                <button className='alternativa' type="submit">Alternativa 5</button>
                            </div>

                        </div>
                    </div>
                </div>
            </div>
        </div>
    ) 
}

export default Perguntas
